        and trade_no = #{query.tradeNo,jdbcType=VARCHAR}


        and member_id = #{query.memberId,jdbcType=VARCHAR}


        and trade_type = #{query.tradeType,jdbcType=INTEGER}


        and status in (${query.status})


         and unix_timestamp(str_to_date(create_time,'%Y-%m-%d %H:%i:%s'))>=unix_timestamp(str_to_date(#{query.startTime},'%Y-%m-%d %H:%i:%s'))  


         and unix_timestamp(str_to_date(create_time,'%Y-%m-%d %H:%i:%s'))<=unix_timestamp(str_to_date(#{query.endTime},'%Y-%m-%d %H:%i:%s'))  


ORDER BY create_time desc

    limit 0,#{limit}

SELECT trade_price AS tradePrice, SUM(trade_remain_number) as tradeNumberSum, SUM(trade_remain_amount) as tradeAmountSum FROM t_mexc_entrust trust WHERE market_id=#{query.marketId,jdbcType=VARCHAR} and trade_vcoin_id=#{query.vcoinId,jdbcType=VARCHAR} and trade_remain_number > 0 and trust.trade_type = #{query.tradeType,jdbcType=INTEGER}
and status in (${query.status})
GROUP BY trust.trade_price ASC
GROUP BY trust.trade_price DESC
limit 0,#{limit}

SELECT
SUM(trade_remain_amount) as tradeAmountSum
FROM
t_mexc_entrust trust
WHERE
market_id=#{query.marketId,jdbcType=VARCHAR}
and trade_vcoin_id=#{query.vcoinId,jdbcType=VARCHAR}
and trade_remain_number > 0

    and trust.trade_type = #{query.tradeType,jdbcType=INTEGER}


    and status in (${query.status})

SELECT * FROM t_mexc_entrust trust,t_mexc_member member where trust.member_id = member.id
and trust.trade_no = #{query.tradeNo,jdbcType=VARCHAR}
and trust.member_id = #{query.memberId,jdbcType=VARCHAR}
and member.account like CONCAT('%', #{query.account}, '%')
and trust.trade_type = #{query.tradeType,jdbcType=INTEGER}
and trust.status in (${query.status})
and unix_timestamp(str_to_date(trust.create_time,'%Y-%m-%d %H:%i:%s'))>=unix_timestamp(str_to_date(#{query.startTime},'%Y-%m-%d %H:%i:%s'))
and unix_timestamp(str_to_date(trust.create_time,'%Y-%m-%d %H:%i:%s'))<=unix_timestamp(str_to_date(#{query.endTime},'%Y-%m-%d %H:%i:%s'))
ORDER BY trust.create_time desc
